CREATE OR REPLACE VIEW mktg_ops_vws.bz_arcpg_response_typ AS 
SELECT bz_arcpg_response_typ.response_typ_key, bz_arcpg_response_typ.response_typ_cd, CASE WHEN (bz_arcpg_response_typ.response_typ_key = 26) THEN 'Downloaded Electronic Wills Guide'::character varying WHEN (bz_arcpg_response_typ.response_typ_key = 28) THEN 'Beneficiary Designation'::character varying WHEN (bz_arcpg_response_typ.response_typ_key = 44) THEN 'Forced into Cultivation – Wills'::character varying ELSE bz_arcpg_response_typ.response_typ_dsc END AS response_typ_dsc, bz_arcpg_response_typ.response_ctg_cd, bz_arcpg_response_typ.response_ctg_dsc, bz_arcpg_response_typ.srcsys_trans_ts, bz_arcpg_response_typ.row_stat_cd, bz_arcpg_response_typ.appl_src_cd, bz_arcpg_response_typ.load_id, bz_arcpg_response_typ.dw_trans_ts FROM ddcoe_vws.bz_arcpg_response_typ UNION ALL SELECT bz_arcpg_response_typ.response_typ_key, bz_arcpg_response_typ.response_typ_cd, CASE WHEN (bz_arcpg_response_typ.response_typ_key = 26) THEN 'Downloaded Electronic Wills Guide'::character varying WHEN (bz_arcpg_response_typ.response_typ_key = 28) THEN 'Beneficiary Designation'::character varying WHEN (bz_arcpg_response_typ.response_typ_key = 44) THEN 'Forced into Cultivation – Wills'::character varying ELSE bz_arcpg_response_typ.response_typ_dsc END AS response_typ_dsc, bz_arcpg_response_typ.response_ctg_cd, bz_arcpg_response_typ.response_ctg_dsc, bz_arcpg_response_typ.srcsys_trans_ts, bz_arcpg_response_typ.row_stat_cd, bz_arcpg_response_typ.appl_src_cd, bz_arcpg_response_typ.load_id, bz_arcpg_response_typ.dw_trans_ts FROM mktg_ops_tbls.bz_arcpg_response_typ WITH NO SCHEMA BINDING;