CREATE OR REPLACE VIEW mktg_ops_vws.bz_comnictn_src AS 
SELECT a.comnictn_src_key, a.nk_comnictn_src_cd, a.comnictn_src_dsc, CASE WHEN (b.new_src_cd IS NULL) THEN a.nk_comnictn_src_cd ELSE b.new_src_cd END AS pg_nk_comnictn_src_cd, CASE WHEN (b.new_src_cd IS NULL) THEN a.comnictn_src_dsc ELSE b.new_src_dsc END AS pg_comnictn_src_dsc, a.nk_comnictn_src_cd AS orig_nk_comnictn_src_cd, a.comnictn_src_dsc AS orig_comnictn_src_dsc, a.comnicatn_src_cst_amt, a.mrktng_activity_key, a.mktcmpgn_typ_key, a.mrktng_initiative_key, a.mrktng_effort_num, a.comnicatn_src_sgmnt_num, a.fund_key, a.gl_debit_acct_key, a.gl_credit_acct_key, a.nk_ta_gl_debit_acct, a.nk_ta_gl_credit_acct, a.channel_typ_key, a.mrktng_pckge_typ_key, a.purpose_typ_key, a.mrktng_list_key, a.evnt_key, a.mail_dt, a.fcc_key, a.srcsys_trans_ts, a.row_stat_cd, a.appl_src_cd, a.load_id, a.dw_trans_ts FROM (ddcoe_vws.bz_comnictn_src a LEFT JOIN (SELECT pg_src_convrsn.orig_src_cd, pg_src_convrsn.orig_src_dsc, pg_src_convrsn.new_src_cd, pg_src_convrsn.new_src_dsc FROM mktg_ops_tbls.pg_src_convrsn) b ON (((b.orig_src_cd)::text = (a.nk_comnictn_src_cd)::text))) WITH NO SCHEMA BINDING;