CREATE OR REPLACE VIEW mktg_ops_vws.bz_dim_channel AS 
SELECT DISTINCT CASE WHEN (adb_nmsdelivery.imessagetype = 0) THEN 1 WHEN (adb_nmsdelivery.imessagetype = 1) THEN 2 WHEN (adb_nmsdelivery.imessagetype = 2) THEN 3 WHEN (adb_nmsdelivery.imessagetype = 3) THEN 4 WHEN (adb_nmsdelivery.imessagetype = 4) THEN 5 WHEN (adb_nmsdelivery.imessagetype = 5) THEN 6 WHEN (adb_nmsdelivery.imessagetype = 20) THEN 7 WHEN (adb_nmsdelivery.imessagetype = 25) THEN 8 WHEN (adb_nmsdelivery.imessagetype = 41) THEN 9 WHEN (adb_nmsdelivery.imessagetype = 42) THEN 10 WHEN (adb_nmsdelivery.imessagetype = 120) THEN 11 ELSE 12 END AS channel_key, adb_nmsdelivery.imessagetype AS channel_id, CASE WHEN (adb_nmsdelivery.imessagetype = 0) THEN 'Email'::character varying WHEN (adb_nmsdelivery.imessagetype = 1) THEN 'SMS'::character varying WHEN (adb_nmsdelivery.imessagetype = 2) THEN 'Phone'::character varying WHEN (adb_nmsdelivery.imessagetype = 3) THEN 'Direct Mail'::character varying WHEN (adb_nmsdelivery.imessagetype = 4) THEN 'Fax'::character varying WHEN (adb_nmsdelivery.imessagetype = 5) THEN 'Agency'::character varying WHEN (adb_nmsdelivery.imessagetype = 20) THEN 'Facebook'::character varying WHEN (adb_nmsdelivery.imessagetype = 25) THEN 'Twitter'::character varying WHEN (adb_nmsdelivery.imessagetype = 41) THEN 'iOS Applications'::character varying WHEN (adb_nmsdelivery.imessagetype = 42) THEN 'Android Application'::character varying WHEN (adb_nmsdelivery.imessagetype = 120) THEN 'Other'::character varying ELSE 'Unknown'::character varying END AS channel_dsc FROM mktg_ops_tbls.adb_nmsdelivery UNION SELECT 0 AS channel_key, 99 AS channel_id, 'Unknown' AS channel_dsc FROM mktg_ops_tbls.adb_enumrtn_lkp WITH NO SCHEMA BINDING;