CREATE OR REPLACE VIEW mktg_ops_vws.bz_dim_email_intrctn_typ AS 
SELECT DISTINCT adb_nmstrackingurl.itype AS email_intrctn_typ_id, CASE WHEN (adb_nmstrackingurl.itype = 0) THEN 'Not defined'::text WHEN (adb_nmstrackingurl.itype = 1) THEN 'Email click'::text WHEN (adb_nmstrackingurl.itype = 2) THEN 'Open'::text WHEN (adb_nmstrackingurl.itype = 3) THEN 'Opt-out'::text WHEN (adb_nmstrackingurl.itype = 4) THEN 'Web'::text WHEN (adb_nmstrackingurl.itype = 5) THEN 'Transaction'::text WHEN (adb_nmstrackingurl.itype = 6) THEN 'Mirror page'::text WHEN (adb_nmstrackingurl.itype = 7) THEN 'Facebook Link'::text WHEN (adb_nmstrackingurl.itype = 8) THEN 'Facebook Action'::text WHEN (adb_nmstrackingurl.itype = 9) THEN 'Twitter Link'::text WHEN (adb_nmstrackingurl.itype = 10) THEN 'Click on mobile notification'::text WHEN (adb_nmstrackingurl.itype = 11) THEN 'Proposition update'::text ELSE 'Unknown Interaction Type'::text END AS email_intrctn_typ_dsc FROM mktg_ops_tbls.adb_nmstrackingurl WITH NO SCHEMA BINDING;