CREATE OR REPLACE VIEW mktg_ops_vws.total_allusers_tblvwslist AS 
SELECT (subquery.schema_name)::character varying AS schemaname, (subquery.table_name)::character varying AS tablename, (subquery.createdby)::character varying AS "owner", (((('mods_bi.'::character varying)::text || ((subquery.schema_name)::character varying)::text) || ('.'::character varying)::text) || ((subquery.table_name)::character varying)::text) AS full_table_name, ((((('mods_bi.'::character varying)::text || ((subquery.schema_name)::character varying)::text) || ('.'::character varying)::text) || ((subquery.table_name)::character varying)::text) || (';'::character varying)::text) AS semicolapnd_ftblnm, subquery.creation_time FROM (SELECT n.nspname AS schema_name, pg_get_userbyid(c.relowner) AS table_owner, c.relname AS table_name, CASE WHEN (c.relkind = 'v'::"char") THEN 'view'::character varying ELSE 'table'::character varying END AS table_type, d.description AS table_description, ci.relcreationtime AS creation_time, pg_get_userbyid(c.relowner) AS createdby FROM ((((pg_class c LEFT JOIN pg_namespace n ON ((n.oid = c.relnamespace))) LEFT JOIN pg_tablespace t ON ((t.oid = c.reltablespace))) LEFT JOIN pg_description d ON (((d.objoid = c.oid) AND (d.objsubid = 0)))) LEFT JOIN pg_class_info ci ON ((ci.reloid = c.oid))) WHERE (((c.relkind = 'r'::"char") OR (c.relkind = 'v'::"char")) AND (((n.nspname = 'mktg_ops_tbls'::name) OR (n.nspname = 'mktg_ops_vws'::name)) OR (n.nspname = 'mktg_stage_tbls'::name))) ORDER BY n.nspname, c.relname) subquery WITH NO SCHEMA BINDING;